mapper.select:
SELECT COUNT(0)
FROM tbl_user


		AND tbl_user.user_id = #{user.userId, jdbcType = INTEGER}


		AND tbl_user.user_name = #{user.userName, jdbcType = VARCHAR}


		AND tbl_user.user_sex = #{user.userSex, jdbcType = VARCHAR}


		AND tbl_user.user_age = #{user.userAge, jdbcType = INTEGER}


		AND tbl_user.user_tel = #{user.userTel, jdbcType = INTEGER}

SELECT
tbl_user.user_id,
tbl_user.user_name,
tbl_user.user_sex,
tbl_user.user_age,
tbl_user.user_tel,
FROM tbl_user


		AND tbl_user.user_id = #{user.userId, jdbcType = INTEGER}


		AND tbl_user.user_name = #{user.userName, jdbcType = VARCHAR}


		AND tbl_user.user_sex = #{user.userSex,jdbcType = VARCHAR}


		AND tbl_user.user_age = #{user.userId, jdbcType = INTEGER}


		AND tbl_user.user_tel = #{user.userTel, jdbcType = INTEGER}


		LIMIT #{user.rowIndex, jdbcType = INTEGER},#{user.rowCount,jdbcType = INTEGER}
